sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();
var tomorrow = gs.daysAgo(-1);
gs.info("sammie: typeof tomorrow = " + typeof tomorrow);

// Query the database for NeedIt Task records with Due date field values within
// the next day.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var dueSoons = new GlideRecord('x_58872_needit_needit_task');
dueSoons.addQuery('due_date', '>', rightNow);
dueSoons.addQuery('due_date', '<=', tomorrow);
dueSoons.addQuery('state', '!=', 3);

dueSoons.query();
// Write a log message for each due soon NeedIt Task Record
while (dueSoons.next()) {
    gs.info("sammie: Due soon NeedIt Task record = " + dueSoons.number);
	gs.eventQueue("x_58872_needit.dueSoonNeedItTask", dueSoons, dueSoons.number, gs.getUserName());
}
